customCSS:
@font-face {
	font-family: 'DepotNew';
	src: url('/helpdesk/s/sfsites/c/resource/DepotNewFont') format('opentype');
}

/* Resize the Featured Topics on the Homepage - 25% 4 columns - 14-09-2021 */
li.topicItem.forceTopicFeaturedTopicItem {
	width: 25%;
	height: 100px;
}

/* Remove the Follow Buttons from Cases - 14-09-2021 */
.slds-button.slds-button--neutral.not-selected.slds-not-selected {
    display:none;
}
.slds-button.slds-button--neutral.is-selected.slds-is-selected {
    display:none;
}

/* Resize the Close Case and Re-Open Case Buttons - 50% - 14/09/2021 */
.slds-button.slds-button--neutral.uiBlock.resolveButton.uiButton {
    width: 50%;
    margin: 0 auto;
}
.slds-button.slds-button--neutral.uiBlock.reopenButton.uiButton {
    width: 50%;
    margin: 0 auto;
}

/* My Cases Button - 14-09-2021 */
.button {
  	background-color: #0047BB;
  	border: 2px solid #0047BB;
    border-radius: 2px;
 	color: #ffffff;
  	width: 50%;
    padding: 8px 0px;
  	text-align: center;
  	text-decoration: none;
  	display: inline-block;
 	font-size: 12px;
	margin: 0px 2px;
  	cursor: pointer;
}
.button1 {
  	background-color: #0047BB; 
  	color: #ffffff;
}
.button1:hover {
  	background-color: #003095;
  	color: #ffffff;
    border: 2px solid #003095;
}

/* All Topics Button - 14-09-2021 */
.button-topic {
  	background-color: #0047BB;
  	border: 2px solid #0047BB;
    border-radius: 2px;
 	color: #ffffff;
  	width: 50%;
    padding: 8px 0px;
  	text-align: center;
  	text-decoration: none;
  	display: inline-block;
 	font-size: 14px;
  	margin: 0px 2px;
  	cursor: pointer;
}
.button1 {
  	background-color: #0047BB; 
  	color: #ffffff;
}
.button1:hover {
  	background-color: #003095;
  	color: #ffffff;
    border: 2px solid #003095;
}

/* Login Home Button - 14-09-2021 */
.button-login {
  	background-color: #0047BB;
  	border: 2px solid #0047BB;
    border-radius: 2px;
 	color: #ffffff;
    width: 100%;
    padding: 8px 0px;
  	text-align: center;
  	text-decoration: none;
    font-weight: bold;
  	display: inline-block;
 	font-size: 16px;
  	margin: 0px 0px;
  	cursor: pointer;
}
.button1 {
  	background-color: #0047BB; 
  	color: #ffffff;
}
.button1:hover {
  	background-color: #003095;
  	color: #ffffff;
    border: 2px solid #003095;
}

