customCSS:
/* Community Font */
@font-face {
	font-family: 'DepotNew';
	src: url('/community/s/sfsites/c/resource/DepotNewFont') format('opentype');
}

/* Generic Formatting - 17/03/2022 */
/* Sidebar Formatting - 28/02/2022 */
.slds-col--padded.slds-size--12-of-12.slds-medium-size--4-of-12.slds-medium-order--1.slds-large-size--4-of-12.slds-large-order--1.comm-layout-column {
    background-color: #F2F2F2;
    padding: 0px 50px;
}
/* Contact Support Request Footer */
/* EBS Toolbox Support Request Button - 15/02/2022 */
.button-ebs-support {
    border: 2px solid #0078FF;
    background-color: #0078FF;
    text-align: center;
    padding: 10px 0px;
    width: 48%;
}
.button-ebs-support:hover{
    border: 2px solid #0064FF;
    background-color: #0064FF;
    text-align: center;
    padding: 10px 0px;
    width: 48%;
}
/* Support Request Button Space - 10/03/2022 */
.support-button-space {
    border: 0px solid #0047BB;
    width: 4%;
}
.support-button-space:hover {
    border: 0px solid #0047BB;
    width: 4%;
}
/* ERP Cloud Toolbox Support Request Button - 15/02/2022 */
.button-erp-cloud-support {
    border: 2px solid #9900A5;
    background-color: #9900A5;
    text-align: center;
    padding: 10px 0px;
    width: 48%;
}
.button-erp-cloud-support:hover{
    border: 2px solid #FF00BE;
    background-color: #FF00BE;
    text-align: center;
    padding: 10px 0px;
    width: 48%;
}
/* One Column Page Layout Padding - 10/03/2022 */
.slds-col--padded.comm-content-header.comm-layout-column {
    padding-left: 50px;
    padding-right: 50px;
}
.slds-col--padded.contentRegion.comm-layout-column {
    padding-left: 50px;
    padding-right: 50px;
}
.slds-col--padded.comm-content-footer.comm-layout-column {
    padding-left: 50px;
    padding-right: 50px;
}
/* Two column Layout Padding - 10/03/2022 */
.slds-col--padded.slds-size--12-of-12.slds-medium-size--8-of-12.slds-medium-order--2.slds-large-size--8-of-12.slds-large-order--2.comm-layout-column {
    padding: 0px 0px;
}

/* Generic Components - 17/03/2022 */
/* Hide Record Popup Preview Panel - 16/02/2022 */
.forcePreviewPanel {
    display: none;
}
.pointer {
     display: none;
}
/* Login Home Button - 14-09-2021 */
.button-login {
  	background-color: #0047BB;
  	border: 2px solid #0047BB;
    border-radius: 2px;
 	color: #ffffff;
    width: 100%;
    padding: 8px 0px;
  	text-align: center;
  	text-decoration: none;
    font-weight: bold;
  	display: inline-block;
 	font-size: 16px;
  	margin: 0px 0px;
  	cursor: pointer;
}
.button1 {
  	background-color: #0047BB; 
  	color: #ffffff;
}
.button1:hover {
  	background-color: #003095;
  	color: #ffffff;
    border: 2px solid #003095;
}
/* Sidebar Toolbox Formatting - 10/03/2022 */
.ebs-toolbox-headers{
    border: 0px solid #D2D2D2;
    border-radius: 5px 5px 0px 0px;
    background-color: #0078FF;
    text-align: left;
	font-size: 12px;
}
.erp-cloud-toolbox-headers {
    border: 0px solid #D2D2D2;
    border-radius: 5px 5px 0px 0px;
    background-color: #9900A5;
    text-align: left;
    font-size: 12px;
}
.toolbox-button-tile {
    border: 0px solid #D2D2D2;
    text-align: left;
    padding: 10px 20px;
}
.toolbox-button-tile:hover {
    border: 0px solid #D2D2D2;
	background-color: #e6edf8;
    text-align: left;
    padding: 10px 20px;
}
/* All Topics Button Formatting - 10/03/2022 */
.button-all-topics {
    border: 0px solid #0047BB;
    border-radius: 5px;
    background-color: #0047BB;
    text-align: center;
    padding: 10px 10px;
    width: 200px;
}
.button-all-topics:hover{
    border: 0px solid #63666A;
    border-radius: 5px;
    background-color: #63666A;
    text-align: center;
    padding: 10px 10px;
    width: 200px;
}

/* Home Page */
/* Section Headers - 08/03/2022 */
.section-headers{
    background-color: #F2F2F2;
    color: #0047BB;
    font-weight: bold;
    font-size: 28pt;
    padding-top: 20px;
    padding-bottom: 20px;
    text-align: center;
}
/* Toolbox Section - Headers - 08/03/2022 */
.toolbox-headers{
    color: #ffffff;
    border: 0px solid #ffffff;
    text-align: center;
}
.toolbox-icons{
    width: 50px;
    height: 50px;
}
/* Toolbox Section - 2 column responsive layout - 25/02/2022 */
.column {
	float: left;
	width: 50%;
}
.row:after {
	content: "";
	display: table;
	clear: both;
}
@media screen and (max-width: 600px) {
	.column {
      	width: 100%;
		padding: 10px;
	}
}
/* Toolbox Section - EBS Buttons and Gradient - 25/02/2022 */
.button-homepage-ebs:hover {
    border: 0px solid #D2D2D2;
    background-color: #D2D2D2;
    text-align: center;
    padding: 10px 0px;
    width: 35%;
    color: #0047BB;
}
.button-homepage-ebs {
    border: 0px solid #0078FF;
    background-color: #0078FF;
    text-align: center;
    padding: 10px 0px;
    width: 35%;
    color: #ffffff;
}
.gradient-ebs {
    height: 500px;
    background-image: linear-gradient(to right, #0078FF, #0047BB);
}
/* Toolbox Section - ERP Cloud Buttons and Gradient - 25/02/2022 */
.button-homepage-erp-cloud:hover {
    border: 0px solid #D2D2D2;
    background-color: #D2D2D2;
    text-align: center;
    padding: 10px 0px;
    width: 35%;
}
.button-homepage-erp-cloud {
    border: 0px solid #FF00BE;
    background-color: #FF00BE;
    text-align: center;
    padding: 10px 0px;
    width: 35%;
}
.gradient-erp-cloud {
	height: 500px;
    background-image: linear-gradient(to right, #FF00BE, #9900A5);
}
/* Product Topics Section - 3 column responsive layout - 25/02/2022 */
.columns {
  float: left;
  padding: 10px;
}
.left, .right {
  width: 49%;
}
.middle {
  width: 2%;
}
.rows:after {
  content: "";
  display: table;
  clear: both;
}
@media screen and (max-width: 768px) {
	.columns {
		width: 100%;
	}
	.middle {
    	display: none;    
    }
}
/* Product Topics Section - Headers */
.ebs-product-topic-headers{
    border: 0px solid #D2D2D2;
    border-radius: 10px 10px 0px 0px;
    background-color: #0078FF;
    text-align: center;
}
.erp-cloud-product-topic-headers{
    border: 0px solid #D2D2D2;
    border-radius: 10px 10px 0px 0px;
    background-color: #9900A5;
    text-align: center;
}
/* Product Topics Section - Topic Buttons */
.button-topic-tile:hover{
    border: 1px solid #D2D2D2;
	background-color: #e6edf8;
    text-align: center;
    padding: 20px 0px;
    width: 30%;
}
.button-topic-tile {
    border: 1px solid #D2D2D2;
    background-color: #FFFFFF;
    text-align: center;
    padding: 20px 0px;
    width: 30%;
}

/* Support Requests Page */
/* Trending Articles Formatting - 09/03/2022 */
.slds-p-vertical_small.slds-border_bottom.slds-hyphenate {
    padding: 10px 20px;
    text-decoration: none;
	border: 0px solid #D2D2D2;
}
.slds-p-vertical_small.slds-border_bottom.slds-hyphenate:hover {
    padding: 10px 20px;
    text-decoration: none;
	border: 0px solid #D2D2D2;
	background-color: #e6edf8;
    font-size: 14px;
    font-weight: bold;
}
.slds-text-body_small {
    font-size: 14px;
    font-weight: bold;
    color: #63666A;
}
.slds-text-body_small:hover {
    font-size: 14px;
    font-weight: bold;
    color: #63666A;
}
.selfServiceTopicTrendingArticles42>h2 {
    padding: 10px 10px;
	font-size: 20px;
    font-weight: bold;
    color: #0047BB;
}
/* Suggested Articles Formatting - 09/03/2022 */
.header-message.slds-p-top_small.slds-align-middle.slds-text-align_center {
    text-align: left;
	padding: 10px 10px;
	font-size: 20px;
    font-weight: bold;
    color: #0047BB;
}
.slds-p-vertical_small.slds-border_bottom.caseDeflectionListItem.slds-text-body_small {
    padding: 10px 20px;
    text-decoration: none;
	border: 0px solid #D2D2D2;
    background-color: #F2F2F2;
}
.slds-p-vertical_small.slds-border_bottom.caseDeflectionListItem.slds-text-body_small:hover {
    padding: 10px 20px;
    text-decoration: none;
	border: 0px solid #D2D2D2;
	background-color: #e6edf8;
}
.slds-hyphenate {
    font-size: 14px;
    font-weight: bold;
    color: #63666A;
}
.slds-list_horizontal.slds-has-dividers_left.slds-text-color_weak {
    display:none;
}
lightning-formatted-date-time{
    display:none;
}
/*ERP Cloud Support Request Header Formatting - 14/03/2022 */
.gradient-support-erp-cloud {
    background-image: linear-gradient(to right, #FF00BE, #9900A5);
}
/*EBS Support Request Header Formatting - 14/03/2022 */
.gradient-support-ebs {
    background-image: linear-gradient(to right, #0078FF, #0047BB);
}
/* Support Request Header Formatting - 16/03/2022 */
.header-support-ebs {
    background-image: url('/community/s/sfsites/c/resource/SupportRequestEBSHeader');    
    background-size: cover;
    padding: 20px 10px;
}
.header-support-erp-cloud {
    background-image: url('/community/s/sfsites/c/resource/SupportRequestERPCloudHeader');    
    background-size: cover;
    padding: 20px 10px;
}
/* Support Request Form Formatting - 14/03/2022 */
.slds-section{
    padding: 0px 30px;
}
/* Submitted Case Page Formatting - 16/03/2022 */
.forceCommunityContactSupportForm {
    padding: 0px 50px;
}

/* Topics Detail Page */
/* Topics Headline Formatting - 07/03/2022 */
.forceCommunityHeadline {
    background-image: url('/community/s/sfsites/c/resource/GenericHeader');    
    background-size: cover;
    padding: 20px 10px;
}
.headlineTitleText {
    font-size: 24px;
    font-weight: bold;
	color: #FFFFFF;
}
.comm-topic__link {
    font-size: 12px;
    font-weight: normal;
	color: #FFFFFF;
}
.comm-topic__link:hover {
    font-size: 12px;
    font-weight: normal;
	color: #FFFFFF;
}
.forceTopicSubTopicNavigation .topic-subTopicItem:not(.topic-last):after {
    font-size: 12px;
    font-weight: normal;
    color: #FFFFFF;
}
/* Topic Breadcrumbs - 11/03/2022 */
.slds-breadcrumb.slds-list--horizontal {
    padding-left: 50px;
}
.slds-text-heading--label {
    color: #0047BB;
	font-size: 20px;
	font-weight: bold;
    text-transform: capitalize;
}
.slds-text-heading--label:hover {
    color: #0047BB;
	font-size: 20px;
	font-weight: bold;
    text-transform: capitalize;
	text-decoration: underline;
}
/*Articles List Formatting - 04/03/2022 */
.selfServiceTopicRelatedArticle {
    padding: 0px 50px;
}
.selfServiceArticleListItem {
    margin: 0px 0px;
}
.article-item.selfServiceArticleListItem {
   	margin: 0px 0px;
    padding-top: 20px;
    padding-bottom: 20px;
    padding-left: 10px;
    padding-right: 10px;
    background-color: #FFFFFF;
}
.article-item.selfServiceArticleListItem:hover {
   	margin: 0px 0px;
    padding-top: 20px;
    padding-bottom: 20px;
    padding-left: 10px;
    padding-right: 10px;
    background-color: #e6edf8;
}
.view-count {
	font-size: 12px;
}
.date {
    display: none;
}
.article-type {
    display: none;
}
.dot {
    display: none;
}

/* Articles Detail Page */
/* Article Headline Formatting - 11/03/2022 */
.slds-icon-utility-left-align-text.headlineIcon.slds-button__icon.slds-icon_container.forceIcon{
    display: none;
}
/* Article Fields Formatting - 11/03/2022 */
.selfServiceArticleHeaderDetail {
    display: none;
}
.slds-grid.slds-gutters_small.full.cols-1.forcePageBlockSectionRow:nth-child(4) {
    display: none;
}
.slds-grid.slds-gutters_small.full.cols-1.forcePageBlockSectionRow:nth-child(5) {
    display: none;
}
.test-id__field-label-container {
	padding-bottom: 10px;
}
/* Hide Article Field Labels - 11/03/2022
.test-id__field-label {
    display: none;
}*/
/*Topic Pill Button Formatting - 11/03/2022 */
.slds-pill_container--bare {
    padding: 10px 10px;
}
.slds-pill:hover{
    background-color: #e6edf8;
}
.slds-pill_container--bare.selfServiceArticleTopicList.selfServiceArticleLayout{
    display: inline-block;
    padding: 0px 10px 30px 10px;
}
/*Article Thumbs Up Section Formatting - 11/03/2022 */
.slds-box--x-small {
    background-color: #e6edf8;
    padding: 20px 20px;
}
.slds-media__body.slds-truncate {
    font-size: 16px;
	font-weight: bold;
}

/* Case Pages - 16/03/2022 */
/* Remove the Follow Buttons from Cases - 14-09-2021 */
.slds-button.slds-button--neutral.not-selected.slds-not-selected {
    display:none;
}
.slds-button.slds-button--neutral.is-selected.slds-is-selected {
    display:none;
}
/* Remove Related List Options Icon and Dropdown - 13/01/2022 */
.forceListViewSettingsMenu {
    display:none;
}
/* Remove Related List Filter options Icon - 13/01/2022 */
.quickFilterButton {
    display:none;
}
/* Remove List View Action Dropdown - 14/01/2022 */
.forceVirtualActionMarker.forceVirtualAction{
    display:none;
}
/* Case Record Banner Formatting - 16/03/2022 */
.forceHighlightsPanel {
    background-color: rgb(243, 243, 243);
    border-radius: 0.25rem;
}
/* Resize the Close Case and Re-Open Case Buttons - 50% - 14/09/2021 */
.slds-button.slds-button--neutral.uiBlock.resolveButton.uiButton {
    width: 200px;
    margin: 0 auto;
    border-radius: 5px;
    text-decoration: none;
	font-size: 14px;
}
.slds-button.slds-button--neutral.uiBlock.resolveButton.uiButton:hover {
    width: 200px;
    margin: 0 auto;
    border-radius: 5px;
	background-color: #63666A;
    text-decoration: none;
    font-size: 14px;
}
.slds-button.slds-button--neutral.uiBlock.reopenButton.uiButton {
    width: 200px;
    margin: 0 auto;
    border-radius: 5px;
    text-decoration: none;
    font-size: 14px;
}
.slds-button.slds-button--neutral.uiBlock.reopenButton.uiButton:hover {
    width: 200px;
    margin: 0 auto;
    border-radius: 5px;
    background-color: #63666A;
    text-decoration: none;
    font-size: 14px;
}

/* User Profile Page */
/* Hide User Badge - 17/03/2022 */
.userBadge {
    display: none;
}
/* User Header Formatting - 17/03/2022 */
.userDetailHeader {
    padding: 0px 30px;
}
